mapper.select:
SELECT * FROM product


        AND created_by = #{createdBy}


        AND creation_time >= #{minCreationTime}


        AND creation_time <= #{maxCreationTime}


        AND modified_by = #{modifiedBy}


        AND modification_time >= #{minModificationTime}


        AND modification_time <= #{maxModificationTime}



        AND name LIKE '%' #{name} '%'


        AND category_level1 = #{categoryLevel1}

    AND deleted = 0
