mapper.select:
SELECT * FROM user
JOIN identity ON user.identity = identity.id


    AND name LIKE CONCAT('%', #{name}, '%')


    AND gender = #{gender}


    AND TIMESTAMPDIFF(YEAR, birthday, CURDATE()) >= #{minAge}


    AND TIMESTAMPDIFF(YEAR, birthday, CURDATE()) <= #{maxAge}


    AND role = #{role}

SELECT * FROM user
JOIN identity ON user.identity = identity.id
WHERE user.id = #{id}

SELECT * FROM user
JOIN identity ON user.identity = identity.id
WHERE account = #{account}
